INSERT [dbo].[Producers] ([ProducerId], [ProducerName], [Description], [Site]) VALUES (7, N'Waves Audio', N'Waves Audio Ltd. является разработчиком и поставщиком профессиональных технологий обработки цифрового аудиосигнала и аудиоэффектов, используемых в области записи, микширования, мастеринга, пост-продакшн, трансляции и живого звука. Штаб-квартира компании и основные объекты развития расположены в Тель-Авиве, с дополнительными офисами в США, Китае и Тайване, а также центрами развития в Индии и Украине.', N'http://Waves.com')
