INSERT [dbo].[Plugins] ([PluginId], [PluginName], [ProducerId], [TypePluginId], [PackageId], [Cost], [Description]) VALUES (48, N'OldSkoolVerb', 7, 3, 1, 0, N'Бесплатный плагин эффекта реверберации для Windows и Mac OS.
