INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (2062, N'The Blues Sessions ', 9, N'WAV', 0, N'The Blues Sessions - коллекция великолепных, аутентичных лупов, исполненные величайшими блюзовыми музыкантами. Эти исполнители работали с такими артистами, как Rory Gallagher, Davey Graham, Eric Bibb, Bert Jansch, Louisiana Red, Big Moose Walker и многими другими. Коллекция записана с использованием винтажного оборудования и микрофонов.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (2063, N'Tropical House Songs', 0, N'WAV', 0, N'Tropical House Songs от Fox Samples это горячий набор в серии Must Have Audio, которая приносит вам летнюю атмосферу. Этот набор содержит все необходимое для создания tropical house от начала до конца. Каждый из этих 5 комплектов также содержит one-shot сэмплы и полные элементы, которые упростят вам жизнь. Всего более 2 ГБ материала для создания следующего летнего гимна.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (2064, N'The Sound Of Hammond ', 11, N'WAV', 0, N'The Sound Of Hammond является поразительной коллекцией записей от одного из самых известных музыкальных инструментов современной эры. Играемый опытным Джазовым пианистом и записанный в профессиональных студиях, этот пакет подкрепит любую композицию с чистым старинным Soul.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3041, N'Textural Garage ', 7, N'WAV', 755, N'Textural Garage погружает слушателя в царство потусторонних звуковых текстур, галлюциногенных минимал битов, атмосферных ритмических элементов и записей окружающей среды. Эта коллекция сэмплов содержит оригинальные элементы для создания красивых звуковых ландшафтов, органических ритмов и эмоциональных музыкальных подложек. Коллекция объединяет в себе элементы downtempo electronica, ambient future garage, dub step, hip hop house и techno.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3042, N'Cinematic Textures', 8, N'WAV', 0, N'Cinematic Textures - коллекция текстурных звуковых ландшафтов для продюсеров, пишущих саундтреки, даунтемпо и эмбиент музыку, а также для использования в брейкдаунах. Используя сочетание атмосферных полевых записей, гранулярного синтеза, экспериментальных ревербераций и слоенных сэмплов, эта библиотека собирает в себе полную палитру звуковых текстур.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3043, N'Ultra FX Fills', 8, N'WAV', 0, N'Ultra FX Fills это инновационный набор, который содержит уникальные заполнения, созданные с использованием тонны эффектов. Набор содержит лупы, подходящие для любых музыкальных жанров - всего более 100 заполнений, которые помогут перейти вашим трекам в дроп.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3044, N'Frontline Producer ', 8, N'WAV', 500, N'Frontline Producer с гордостью представляет Funk and Disco Guitars, набор лупов фанковых электрогитар, эксклюзивно записанный Andy Baker. Внутри вы найдете риффы, ритмы, басы, лиды и арпеджио, которые работают вместе как взаимозаменяемые детали в удобно организованных тональностях с вариациями.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3045, N'Afro-Latin Percussion ', 8, N'WAV', 0, N'Afro-Latin Percussion это по-настоящему аутентичная коллекция высокоэнергичных южно-американских и карибских перкуссионных грувов, идеальные для того, чтобы приправить ваши треки, и внести латинский вкус в ваши ритм-треки. Все лупы исполнены перкуссионистом мирового класса в уникальном месте Lightship Studios, сделанной на заказ звукозаписывающей студии, расположенной на 550-тонной лодке.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3046, N'Epic Movie Themes 3 ', 9, N'WAV', 0, N'Epic Movie Themes 3 это вдохновляющий набор сэмплов и MIDI файлов удивительных кинематографических мелодий. Набор содержит гипнотические темы, вдохновленные такими фильмами, как Тёмный рыцарь: Возрождение легенды, Начало, Матрица , Властелин Колец , Гарри Поттер и многими другими.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3047, N'Liquid Trap', 5, N'WAV', 400, N'Loopmasters рады представить Liquid Trap, футуристическую коллекцию трэп грувов с жирными 808 саб басами, энергичными хэтами, снейрами, бигрум синтезаторами и эффектами, организованных в 5 комплектах с различным темпом (146, 100, 180, 145 и 150 BPM).')
