INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3048, N'Soprano Sax 2 ', 2, N'WAV', 0, N'Soprano Sax 2 от Zion Music приносит вам 90 мелодических лупов саксофона. Сэмплы записаны в реальном времени профессиональными музыкантами. Эти мелодии могут быть творчески использованы в различных атмосферных жанрах музыки, включая Ambient, Chillout , Pop, Fusion и Trance.')
