INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3049, N'Audentity ', 8, N'WAV', 0, N'Audentity - Ultimate Vocal Chops представляет собой новую концепцию и обеспечивает свежее звучание воспроизводимый набор вокальной продукции, построен для производителей , композиторов и художников.')
