INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3050, N'Future House ', 3, N'WAV', 0, N'Future House это вдохновляющая коллекция супер-современных звуков, которые несомненно внесут немного летних красок в серые треки. Если вам требуются фанковые ударные, глубокие заводные бас линии, пульсирующие синтезаторы и нарезанный вокал - то этот набор для вас. Этот набор подойдет для создания классически звучащего deep house или свежего EDM. Прекрасно записанные перкуссионные лупы помогут добавить дополнительный грув мощным драм-машинам.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3051, N'Justin and Robin 3', 4, N'WAV', 0, N'Justin and Robin 3 от Fox Samples это продолжение двух первых успешных томов серии. И снова, этот pop/rnb набор представляет собой идеальную смесь звука Justin Timberlake и Robin Thicke. Наполненный фанковыми гитарами, мелодическими аранжировками, душевными клавишными и хрустящими ударными, этот набор содержит все необходимое для создания душевных баллад.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3052, N'Party Anthems', 9, N'WAV', 0, N'Разорвите клубы на части и создайте выброс звукового панча с Party Anthems - универсальным набором свежих и взрывных сэмплов от Prime Loops. Разнообразие это основа данного набора, которое обеспечивает вас широким спектром профессиональных звуков для dance, trance, electro, dubstep и многих других. Набор включает все от мощных битов до синтезаторов и эффектов.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3053, N'Exotic Trap 2', 5, N'WAV', 0, N'Exotic Trap 2 от Fox Samples это продолжение успешного первого тома в серии, которое включает только самые горячие трэп элементы, смешанные с индийскими, арабскими и восточными звуками. Если вы поклонник уникальных звуков, то этот набор определенно для вас.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3054, N'Trap Drummer 25 Kits', 7, N'WAV', 0, N'Hex Loops с гордостью представляет Trap Drummer 25 Kits, свежую коллекцию из 25 барабанных строительных комплектов, в состав которых входит 140 WAV-файлов. Данная подборка семплов содержит в себе лупы в стиле таких исполнителей как Lex Luger, Waka Flocka Flames, Kanye West, Rick Ross, T.I., Chief Keef и многих других. Если Вы поклонник Trap музыки, тогда эта коллекция именно для Вас!')
