INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3055, N'Professor Ethnik Trap ', 5, N'WAV', 20, N'Professor Ethnik Trap это набор из 5 пылающих комплектов, разработанных, чтобы вдохновлять вас на создание аутентичных этнических трэп треков.')
