INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3056, N'Exotic Trap ', 3, N'WAV', 0, N'Exotic Trap от Fox Samples это новая серия, которая покорит чарты в кротчайшие сроки. Включая только самые горячие элементы Trap музыки, смешанные с индийскими, арабскими и восточными звуками, этот набор продолжает серию этнических трэп комплектов для создания ваших собственных уникальных треков.')
