INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3057, N'Sample Tools', 7, N'WAV', 0, N'Sample Tools и Cr2 Records представляют вам второй по счету релиз Deep Analogue House, который включает более 600 Мб убийственного аудио материала, созданного с помощью классических синтезаторов и драм-машин TB 303, Minimoog Voyager XL, Moog Sub Phatty, Moog Minitaur, Access Virus, Nord Lead 3, Juno 60, TR 808, TR 909, TR 707. Внутри вас ждут качающие бас-линии, пробивные бочки, глубокие и мелодические синтезаторы, петли винтажных ударных.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3058, N'Evolving Deep House', 3, N'WAV', 0, N'Evolving Deep House обеспечивает комплексное решение для производителей Deep House. Душевные звуки режущей кромки и эмоциональные мелодии . Огромные басовые линии , Испепеляющие Synths, колодки и Beats Все записанные при 122 BPM.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3059, N'Future House Patches', 2, N'WAV', 0, N'Future House Patches содержит более 100 мелодичных Spire пресетов для future house господства. Если вы ищите звуки в стиле EDX, Don Diablo Mr. Belt & Wezol и Oliver Heldens, то этот набор для вас. Набор заполнен жирными FM басами, мелодическими портаменто синтезаторами, эпическими trance лидами, глубокими garge аккордами, широкими подкладами и эффектами.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3060, N'A State Of Trance', 4, N'WAV', 500, N'Ударные   Струнные   Клавишные   Электронные   Вокальные  
Внутри этого продукта вы найдете 12 комплектов, бонусные лупы и многое другое. Вдохновленные самыми горячими мелодиями "A State Of Trance", Mark Sixma, Armin Van Buuren, Gareth Emery, AVA Recordings. Все звуки были подготовлены и обработаны опытной командой производителей, поэтому вы получите только лучшее из лучших. Этот огромный пакет содержит просто все, что нужно для создания собственных замечательных мелодий.')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3061, N'Emotional Piano Themes', 8, N'WAV', 0, N'Singomakers вернулись с новым томом в ультра-вдохновляющей серии эмоциональных тем фортепиано Emotional Piano Themes. Коллекция включает 20 уникальных композиций фортепиано, 40 Wav лупов эмоционального фортепиано, 40 MIDI файлов и 20 полных миксов с темпом 83-180 BPM. Emotional Piano Themes Vol. 5 это великолепный инструмент для каждого продюсера, желающего добавить немного эмоций в свои треки.

')
GO
INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3062, N'Trap Terminators ', 6, N'WAV', 20, N'Trap Terminators это ошеломляющая коллекция Trap сэмплов от Singomakers, которая является ядерным оружием для каждого современного продюсера, сосредоточенного на Trap, Twerk, Future Bass и любых других вариациях трэп музыки. Эта коллекция вдохновлена треками Diplo, Yellow Claw, AWE, Baauer, Rustie, Bad Royale, Creaky Jackals, Dillon Francis, Major Lazer, Pegboard Nerds, Milo & Otis, Kill the Noise, ALESIA, Party Favor, Tisoki, Topi и многих других.

')
