INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3064, N'Explosive Trap Drops', 6, N'WAV', 0, N'Explosive Trap Drops это новый монстр на трэп сцене, заполненный хитами. Zenhiser сковал его корни из различных стилей, включая Festival Trap, Melodic Trap, Trapstyle, Twerk и Hip Hop. Внутри вас ждут безумные басы, свирепые биты, завораживающие синтезаторы и атомные эффекты.')
