INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3065, N'Colossal Kits Vol 2', 4, N'WAV', 0, N'Colossal Kits Vol 2 предлагает массивные сотрясающие бочки, умопомрачительные снеиры, безумную перкуссию и тарелки - всего более 100 звуков ударных в формате WAV.')
