INSERT [dbo].[LibrarySamples] ([LibrarySampleId], [LibraryName], [ProducerId], [Format], [Cost], [Description]) VALUES (3066, N' Trap Serum Presets э', 6, N'WAV', 0, N'Patchworx 75 - Trap Serum Presets это удивительная коллекция передовых басов, лидов и эффектов для Serum. Этот новый набор в серии Patchworx посвящен современным трэп звукам и включает низкие басы, ударные, энергичные эффекты, лиды, синтезаторы, подклады, а также MIDI файлы и WAV лупы.')
